class Article{
  String title;
  String byline;
  String url;
  String imageUrl;
  String publishedDate;
  String summary;

  Article({this.title, this.byline, this.url, this.imageUrl, this.publishedDate,
      this.summary});


  factory Article.fromMap(Map<String, dynamic> map) {
    return  Article(
      title: map['title'],
      byline: map['byline'],
      url: map['url'],
      publishedDate: map['published_date'],
      summary: map['abstract'],
      imageUrl: map['multimedia'].length > 0 ? map['multimedia'][3]['url'] : 'data:image/jpeg;base64,'
    );
  }
}

